<script lang="ts">
    import { onMount } from 'svelte';

    const MAX_RETRIES = 3;
    const RETRY_DELAY_MS = 1000;
    const pollInterval = 30000; // 30 seconds
    const API_URL = 'https://vscode.snehasish.xyz/api/users/17552586880985304878';

    // base64 because yes.
    const vscodeLogo = 'data:image/png;base64,';

    let isLoading = true;
    let errorCode: string | undefined;
    let workspace: string | undefined;
    let fileName: string | undefined;
    let language: string | undefined;
    let extension: string | undefined;
    let status: string | undefined;
    let errors: number | string | undefined;

    let online: boolean | undefined;

    function sleep(ms: number) {
        return new Promise((r) => setTimeout(r, ms));
    }

    function capitalizeProperly(input: string) {
        const knownReplacementThings: Record<string, string> = {
            "typescript": "TypeScript",
            "javascript": "JavaScript",
            "html": "HTML",
            "css": "CSS",
            "svelte": "Svelte",
            "json": "JSON",
        }

        let replacement: string | undefined = knownReplacementThings[input.toLowerCase()];

        return replacement || input.charAt(0).toUpperCase() + input.slice(1);
    }

    async function loadVSCodeStatusWithRetry() {
        let attempt = 0;
        let lastErrorCode: string | undefined;

        while (attempt < MAX_RETRIES) {
            try {
                const res = await fetch(API_URL);
                if (!res.ok) {
                    lastErrorCode = res.status.toString();
                    throw new Error('HTTP error ' + res.status);
                }

                const data = await res.json();

                workspace = data.current?.workspace?.name;
                fileName = data.current?.file?.name;
                language = capitalizeProperly(data.current?.file?.language);
                extension = data.current?.file?.extension ? `.${data.current?.file?.extension}` : '';
                status = data.current ? 'Online' : 'Offline';
                errors = data.current?.file?.errors;

                online = status.toLowerCase() === 'online';

                errorCode = undefined;
                return true;
            } catch {
                lastErrorCode = 'INTERNAL_ERROR';
            }

            attempt++;
            if (attempt < MAX_RETRIES) {
                await sleep(RETRY_DELAY_MS);
            }
        }

        errorCode = lastErrorCode;
        return false;
    }

    onMount(async () => {
        await loadVSCodeStatusWithRetry();
        isLoading = false;

        setInterval(loadVSCodeStatusWithRetry, pollInterval);
    });
</script>

{#if isLoading}
    <div class="vscode-status-card">
        <div class="card-header">
            <div class="status-indicator loading"></div>
            <h3 class="status-text">Loading VSCode status...</h3>
        </div>
    </div>
{:else if errorCode}
    <div class="vscode-status-card error" aria-live="polite">
        <div class="card-header">
            <div class="status-indicator error" aria-hidden="true"></div>
            <h3 class="status-text">Error loading VSCode status</h3>
        </div>
        <div class="card-content">
            <div class="generic-error">Please try again later.</div>
        </div>
    </div>
{:else}
    <div class="vscode-status-card">
        <div class="card-header">
            {#if online}
                <div class="status-indicator online" aria-hidden="true"></div>
            {:else}
                <div class="status-indicator" aria-hidden="true"></div>
            {/if}
            <h3 class="status-text">{status}</h3>
        </div>

        <div class="card-content">
            {#if online}
            <div class="vscode-logo">
                <img loading="lazy" src={vscodeLogo} alt="VSCode Logo" />
            </div>
            {/if}

            <div class="track-info">
                {#if online}
                    {#if fileName && extension}
                        <div class="file"><span class="darker-text">Editing</span> {fileName}{extension}</div>
                    {/if}
                    <div class="workspace"><span class="darker-text">Workspace:</span> {workspace ? workspace : 'Unknown'}</div>
                    <div class="language"><span class="darker-text">Language:</span> {language ? language : 'Unknown'}</div>
                    <div class="errors"><span class="darker-text">Errors in file:</span> {errors ? errors : 'None'}</div>
                {:else}
                    <div class="workspace">User is currently offline.</div>
                {/if}
            </div>
        </div>
    </div>
{/if}

